import React from 'react';
import { useParams } from 'react-router-dom';
import SchoolBanner from './SchoolBanner';
import SchoolTopNav from './SchoolTopNav';
import ClubSearch from './ClubSearch';
import ClubList from './ClubList';

const schools = [
  { id: 1, name: 'Cal State Fullerton' },
  { id: 2, name: 'Cal Poly Pomona' },
  // ... other schools
];

const clubsData = [
  { id: 1, name: 'Club 1' },
  { id: 2, name: 'Club 2' },
  // ... other clubs
];

const SchoolPage: React.FC = () => {
  const { schoolId } = useParams<{ schoolId: string }>();

  // Find the corresponding school object based on the schoolId
  const selectedSchool = schools.find(school => school.id === parseInt(schoolId, 10));

  return (
    <div>
      <SchoolTopNav />
      {selectedSchool ? <SchoolBanner schoolName={selectedSchool.name} /> : null}
      <ClubSearch />
      <ClubList clubs={clubsData} />
    </div>
  );
};

export default SchoolPage;
